create table producto (id integer generated by default as identity, imagen_frontal varchar(255), imagen_trasera varchar(255), nombre_producto varchar(255), porcentaje_descuento integer, precio integer, precio_con_descuento integer, primary key (id));
create table producto_detalle (id integer not null, descripcion varchar(255), primary key (id));
create table producto_detalle_listado_imagenes (producto_detalle_id integer not null, listado_imagenes varchar(255));

alter table producto_detalle add constraint FK91b5gtxbh9b3og9hpxj6ucdke foreign key (id) references producto;
alter table producto_detalle_listado_imagenes add constraint FKen51vbnqe2gspalqxwu5mnr2n foreign key (producto_detalle_id) references producto_detalle;


